function Header() {
  return (
    <div className="hero-section">
      <div className="hero-content">
        <h1 className="hero-title">Elise Pitre - Co-op Portfolio</h1>
        <nav>
          <ul className="hero-links">
            <li><a href="/">Home</a></li>
            <li><a href="/report">Work Term 1</a></li>
          </ul>
        </nav>
      </div>
    </div>
  );
}

export default Header;

